targets.query:
from(bucket: "CER")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["device_name"] == "LA66N717255")
  |> filter(fn: (r) =>  r["_measurement"] =~ /device_frmpayload_data_soilHumidity/)
  |> group()
  |> last()
  |> map(fn: (r) => ({
    _time:r._time,
    "Soil Humidity": r._value
  }))

from(bucket: "CER")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] =~ /device_frmpayload_data_soilTemperature/)
  |> filter(fn: (r) => r["device_name"] == "LA66N717255")
  |> aggregateWindow(every: 5m, fn: mean, createEmpty: false)
  |> map(fn: (r) => ({
    _time:r._time,
    "Soil Temperature": r._value
  }))

from(bucket: "weatherapi")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "home")
  |> filter(fn: (r) => r["_field"] == "main_temp" or r["_field"] == "main_temp_max" or r["_field"] == "main_temp_min")
  |> map(fn: (r) => ({
    _time:r._time,
    _field: if r._field == "main_temp" then "Weather Station Temp" else
            if r._field == "main_temp_max" then "Weather Station Max Temp" else 
            if r._field == "main_temp_min" then "Weather Station Min Temp" else "n/a",
    _value: r._value
  }))
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)
  |> yield(name: "mean")

